mapper.insert:
INSERT 
  INTO MEMBER
VALUES ( 
       #{ id }
       , #{ pwd }
       , #{ name }
       , #{ nickname }
       , #{ email }
       , #{ sex }
       , #{ phone }
       , #{ address }
       , sysdate
       , sysdate
       , default
       , null
       , null
       , null
       , default
       , default
          )

mapper.select:
SELECT
       *
  FROM MEMBER
 WHERE NAME = #{ name }
   AND EMAIL = #{ email }

SELECT
       *
  FROM MEMBER
 WHERE ID = #{ id }
   AND EMAIL = #{ email }
