mapper.update:
update notice
   set ncount = ncount + 1
   where nid = #{ NID }

mapper.insert:
insert into notice

values (seq_nid.nextval
		, #{ ntype }
		, #{ nwriter }
		, #{ ntitle }
		, #{ ncontent }
		, #{ n_file_name }
		, #{ n_rfile_name }
		, sysdate
		, sysdate
		, default
		, default)


values (seq_nid.nextval
		, #{ ntype }
		, #{ nwriter }
		, #{ ntitle }
		, #{ ncontent }
		, null
		, null
		, sysdate
		, sysdate
		, default
		, default)

mapper.update:
update notice
set ntitle = #{ ntitle },
   ncontent = #{ ncontent }

   , n_file_name = #{ n_file_name }


   , n_file_name = null

where nid = #{ nid }
