mapper.select:
select *
  from blame
 where blame_no = #{ blame_no }

mapper.insert:
insert into blame
values 
		  (seq_blid.nextval
		, #{ blame_bid }
		, #{ blame_type }
		, #{ mm_nickname }
		, #{ target_nickname }
		, sysdate
		, #{ blame_content }
		, #{ blame_ip }
		)

mapper.select:
select count(*) 
  from Blame
 where blame_date
 between #{ begin }
     and #{ end }
