mapper.select:
SELECT *
FROM BOARD
WHERE B_STATUS = 'Y'
AND P_PROID = #{ p_proid }
and b_create_date between #{ begin } and #{ end }
ORDER BY BID DESC

select count(*) from p_board
where ptitle like '%' || #{
keyword } || '%'

select count(*) from p_board
where pwriter like '%' || #{
keyword } || '%'

select
count(*) from p_board
where p_date between #{ begin } and #{ end }

mapper.insert:

INSERT 
  INTO P_BOARD

values (
        seq_pid.nextval
      , #{ p_proid }
      , #{ pwriter }
      , #{ ptitle }
      , #{ pcontent }
      , #{ p_file_name }
      , #{ p_rfile_name }
      , sysdate
      , sysdate
      , default
      , default
       )


values (
        seq_pid.nextval
       , #{ p_proid }
      , #{ pwriter }
      , #{ ptitle }
      , #{ pcontent }
      , null
      , null
      , sysdate
      , sysdate
      , default
      , default
       )

mapper.update:
update p_board set ptitle = #{ ptitle }, pcontent = #{ pcontent }, P_MODFIY_DATE = sysdate
, p_file_name = null, p_rfile_name = null
, p_file_name = #{ p_file_name }, p_rfile_name = #{ p_rfile_name }
where pid = #{ pid }

UPDATE PROGRESS
SET PRO_DEADLINE = #{ pro_deadline }
WHERE PRO_ID = #{ pro_id }

UPDATE PROGRESS
SET PRO_PROCESS = PRO_PROCESS - 10
WHERE PRO_ID = #{ pro_id }

UPDATE PROGRESS
SET PRO_PROCESS = PRO_PROCESS + 10
WHERE PRO_ID = #{ pro_id }

UPDATE PROGRESS
SET PRO_STATUS = 'C'
WHERE PRO_ID = #{ pro_id }
